var map;function init(){map=new OpenLayers.Map("map");map.addControl(new OpenLayers.Control.LayerSwitcher());var a=new OpenLayers.Layer.Google("Google Physical",{type:google.maps.MapTypeId.TERRAIN});var e=new OpenLayers.Layer.Google("Google Streets",{numZoomLevels:20});var c=new OpenLayers.Layer.Google("Google Hybrid",{type:google.maps.MapTypeId.HYBRID,numZoomLevels:20});var b=new OpenLayers.Layer.Google("Google Satellite",{type:google.maps.MapTypeId.SATELLITE,numZoomLevels:22});map.addLayers([a,e,c,b]);map.setCenter(new OpenLayers.LonLat(10.2,48.9).transform(new OpenLayers.Projection("EPSG:4326"),map.getProjectionObject()),5);var d=document.getElementById("animate");d.onclick=function(){for(var f=map.layers.length-1;f>=0;--f){map.layers[f].animationEnabled=this.checked}}};